}
@font-face {
  font-family: "Jost";
  font-style: italic;
  font-weight: 100 900;
  font-display: swap;
  src: url("/80c9573fd8b6bbba88f282976c0046c1.woff2") format("woff2");
}
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html {
  font-size: 100%;
  font-family: "Jost", sans-serif;
}

.container {
  width: 75rem;
  margin: 0px auto;
  padding: 0 2rem;
}

@media (max-width: 62em) {
  .container {
    width: 35rem;
  }
}
@media (max-width: 46em) {
  .container {
    width: 20rem;
  }
}
.header {
  height: 40rem;
  background-image: linear-gradient(-5.71deg, white 49.9%, transparent 50.1%), url("/2f6a780a7ce79c258a4454f2b717eb1a.jpg");
  background-repeat: no-repeat, no-repeat;
  background-position: center 100%, center center;
  background-size: 100vw 10vw, cover;
  vertical-align: middle;
  overflow: hidden;
  position: relative;
}
@media (max-width: 36em) {
  .header {
    background-size: cover;
  }
}

.header__heading-container {
  clear: both;
}

.header__heading-main {
  text-transform: uppercase;
  color: white;
  font-size: 7rem;
  text-align: center;
  line-height: 80%;
  padding-top: 3rem;
  text-shadow: 0 0 0.5rem black;
  animation-name: header__heading-main-font-weight;
  animation-delay: 0.75s;
  animation-duration: 2s;
  animation-timing-function: ease-in-out;
  animation-fill-mode: both;
}
@media (max-width: 48em) {
  .header__heading-main {
    font-size: 3rem;
  }
}

@keyframes header__heading-main-font-weight {
  0% {
    opacity: 0;
    font-weight: 100;
  }
  100% {
    opacity: 1;
    font-weight: 700;
  }
}
.header__heading-sub {
  background-color: rgba(20, 17, 17, 0.808);
  text-transform: uppercase;
  color: aliceblue;
  text-align: center;
  letter-spacing: 0.5rem;
  font-size: 1.75rem;
  margin-top: 1rem;
  text-shadow: 0 0 0.5rem black;
}
@media (max-width: 68em) {
  .header__heading-sub {
    font-size: 1rem;
  }
}
@media (max-width: 48em) {
  .header__heading-sub {
    font-size: 1rem;
  }
}

.header__heading-container-sub {
  text-align: center;
  display: flex;
  justify-content: center;
}

.navigation__list-item {
  list-style-type: none;
  display: block;
  float: left;
}
@media (max-width: 62em) {
  .navigation__list-item--mobile-hidden {
    display: none;
  }
}
.navigation__list-item--menu {
  float: right;
  margin-top: -0.25;
}
.navigation__list-item:hover {
  color: red;
}

.navigation__list-item-link {
  color: white;
  text-decoration: none;
  padding: 0.5rem 0.25rem 0.25rem 0.25rem;
  margin-top: 2rem;
  margin-left: 2rem;
  margin-right: 2rem;
  display: block;
  text-shadow: 0 0 0.5rem black;
  font-size: 1.25rem;
}
.navigation__list-item-link:hover {
  border-bottom: 0.2rem solid blue;
}

.navigation__list-item-logo {
  width: 4rem;
  height: 4rem;
  margin-top: -1.5rem;
  margin-left: 0rem;
  padding-left: 0rem;
  fill: red;
}
.navigation__list-item-logo:hover {
  transform: rotate(45deg);
}

.navigation__list-item-link--logo:hover {
  border-bottom: none;
  vertical-align: middle;
}

.navigation__list-item-link--logo:hover .navigation__list-item-logo {
  fill: black;
}

.motivation {
  margin-top: 2rem;
  color: #2f2f2f;
}

.motivation__column-text {
  float: left;
  width: 33.3%;
}

.motivation__text-heading {
  color: #555454;
  font-weight: 500;
  margin-top: 4rem;
  margin-bottom: 0.5rem;
}
.motivation__text-heading::before {
  content: "";
  width: 3rem;
  height: 0.25rem;
  display: block;
  background-color: #2f2f2f;
  margin-bottom: 0.5rem;
}

.motivation__column-images {
  float: left;
  width: 66.6%;
  position: relative;
}

.motivation-image {
  max-width: 75%;
  position: absolute;
  box-shadow: 0 0 1rem black;
  box-sizing: content-box;
}
.motivation-image--first {
  left: 50%;
  width: 40.3%;
  z-index: 2;
  top: 7rem;
  box-shadow: 0 0 2rem black;
}
.motivation-image--second {
  right: 20%;
  width: 60%;
}
.motivation-image--third {
  right: 0;
  width: 45.3%;
  right: 40%;
  top: 15rem;
  z-index: 1;
}
.motivation-image:hover {
  border: white solid;
}

